mapper.select:
SELECT MEMBER_NAME
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT TRANSACTION_TIME, TRANSACTION_STORE, TRANSACTION_AMT, STORE_ID
FROM TRANSACTION
WHERE ACCOUNT_NUMBER = #{accountNumber}
AND TRANSACTION_TIME BETWEEN #{startDay} AND #{finishDay}

mapper.update:
UPDATE ACCOUNT
SET account_balance=#{balance}
WHERE ACCOUNT_NUMBER=#{accountNumber}

UPDATE TRANSACTION 
SET TRANSACTION_MEMO = #{transactionMemo} 
WHERE TRANSACTION_ID=#{transactionId}

mapper.select:
SELECT ACCOUNT_NUMBER, MEMBER_ID, ACCOUNT_BALANCE, ACCOUNT_INNER, ACCOUNT_BANK
FROM ACCOUNT
WHERE MEMBER_ID=#{memberId}

SELECT account_balance
FROM ACCOUNT
WHERE ACCOUNT_NUMBER=#{accountNumber}

mapper.update:
UPDATE ACCOUNT
SET ACCOUNT_BALANCE = #{balance}
WHERE ACCOUNT_NUMBER = #{accountNumber}
